void Settings__screenViewBase::buttonCallbackHandler(const touchgfx::AbstractButton& src)
{
    if (&src == &buttonWithLabelEN)
    {
        //Interaction7
        //When buttonWithLabelEN clicked execute C++ code
        //Execute C++ code
        Texts::setLanguage(GB);
        btn_Back_language.invalidate();
        buttonWithLabelfrench.invalidate();
        buttonWithLabelEN.invalidate();
    }
    else if (&src == &buttonWithLabelfrench)
    {
        //Interaction6
        //When buttonWithLabelfrench clicked execute C++ code
        //Execute C++ code
        Texts::setLanguage(FRA);
        btn_Back_language.invalidate();
        buttonWithLabelfrench.invalidate();
        buttonWithLabelEN.invalidate();
    }
}

void Settings__screenViewBase::flexButtonCallbackHandler(const touchgfx::AbstractButtonContainer& src)
{
    if (&src == &btn_Back_sound)
    {
        //Interaction5
        //When btn_Back_sound clicked call virtual function
        //Call soundtomain
        soundtomain();
    }
    else if (&src == &btn_Back_language)
    {
        //setsetting2
        //When btn_Back_language clicked call virtual function
        //Call set_setting
        set_setting();
    }
    else if (&src == &btn_Back_displaymode)
    {
        //set_setting1
        //When btn_Back_displaymode clicked call virtual function
        //Call set_setting
        set_setting();
    }
    else if (&src == &DisplayDefault)
    {
        //displaydefault
        //When DisplayDefault clicked call virtual function
        //Call displaydefault
        displaydefault();
    }
    else if (&src == &DisplayChildren)
    {
        //displaychildren
        //When DisplayChildren clicked call virtual function
        //Call displaychildren
        displaychildren();
    }
    else if (&src == &DisplayDark)
    {
        //displaydark
        //When DisplayDark clicked call virtual function
        //Call displaydark
        displaydark();
    }
    else if (&src == &btn_Auto_Diagnostic)
    {
        //iteraction_autodiagnosic
        //When btn_Auto_Diagnostic clicked call virtual function
        //Call goto_autodiagnosic
        goto_autodiagnosic();
    }
    else if (&src == &btn_Display_mode)
    {
        //IT_goto_display
        //When btn_Display_mode clicked call virtual function
        //Call goto_Displaymode
        goto_Displaymode();
    }
    else if (&src == &btn_Time_Date)
    {
        //Interaction_time_date
        //When btn_Time_Date clicked change screen to Settings_Time_Date
        //Go to Settings_Time_Date with no screen transition
        application().gotoSettings_Time_DateScreenNoTransition();
    }
    else if (&src == &btn_Language)
    {
        //IT_language
        //When btn_Language clicked call virtual function
        //Call goto_Language
        goto_Language();
    }
    else if (&src == &btn_Sound)
    {
        //Interaction4
        //When btn_Sound clicked call virtual function
        //Call gotosound
        gotosound();
    }
    else if (&src == &btn_Manual)
    {
        //Interaction11
        //When btn_Manual clicked change screen to Manual
        //Go to Manual with no screen transition
        application().gotoManualScreenNoTransition();
    }
    else if (&src == &btn_Drug_Library)
    {
        //Interaction12
        //When btn_Drug_Library clicked change screen to DrugLibrary
        //Go to DrugLibrary with no screen transition
        application().gotoDrugLibraryScreenNoTransition();
    }
    else if (&src == &btn_Departement)
    {
        //Interaction10
        //When btn_Departement clicked change screen to Departement
        //Go to Departement with no screen transition
        application().gotoDepartementScreenNoTransition();
    }
    else if (&src == &btn_history)
    {
        //Interaction3
        //When btn_history clicked call virtual function
        //Call gotohistory
        gotohistory();
    }
    else if (&src == &btn_Back)
    {
        //gotostart
        //When btn_Back clicked change screen to mainScreen_WithSTART
        //Go to mainScreen_WithSTART with no screen transition
        application().gotomainScreen_WithSTARTScreenNoTransition();
    }
    else if (&src == &btn_Battery)
    {
        //Interaction9
        //When btn_Battery clicked change screen to Battery
        //Go to Battery with no screen transition
        application().gotoBatteryScreenNoTransition();
    }
    else if (&src == &btn_Lock)
    {
        //Lock
        //When btn_Lock clicked change screen to LockScreen
        //Go to LockScreen with no screen transition
        application().gotoLockScreenScreenNoTransition();
    }
    else if (&src == &btn_maintenance)
    {
        //Interaction8
        //When btn_maintenance clicked call virtual function
        //Call gotomaintenance
        gotomaintenance();
    }
}
